    @Override
    public void start() {
        System.out.println("Plane engine started.");
    }

    @Override
    public void stop() {
        System.out.println("Plane engine stopped.");
    }

    @Override
    public void drive() {
        throw new UnsupportedOperationException("Plane cannot drive like a car.");
    }

    @Override
    public void fly() {
        System.out.println("Plane is flying.");
    }

    @Override
    public void dive() {
        throw new UnsupportedOperationException("Plane cannot dive underwater.");
    }
}

class Submarine implements Vehicle {
    @Override
    public void start() {
        System.out.println("Submarine powered up.");
    }

    @Override
    public void stop() {
