        System.out.println("Mage walks gracefully.");
    }

    public void fly() {
        System.out.println("Mage flies magically.");
    }

    public void meleeAttack() {
        System.out.println("Mage does a weak melee attack with staff.");
    }

    public void castSpell() {
        System.out.println("Mage casts a fireball.");
    }

    public void displayRole() {
        System.out.println("Current character: Mage");
    }
}

