    public void walk() {
        System.out.println("Fighter stomps forward.");
    }

    public void fly() {
        System.out.println("Fighter cannot fly!");
    }

    public void meleeAttack() {
        System.out.println("Fighter slashes with a sword!");
    }

    public void castSpell() {
        System.out.println("Fighter cannot cast spells!");
    }

    public void displayRole() {
        System.out.println("Current character: Fighter");
    }
}


class Ranger implements Character {
    public void walk() {
        System.out.println("Ranger moves swiftly.");
    }

    public void fly() {
        System.out.println("Ranger cannot fly!");
    }

    public void meleeAttack() {
        System.out.println("Ranger stabs with a dagger.");
    }

    public void castSpell() {
        System.out.println("Ranger cannot cast spells!");
    }

    public void displayRole() {
        System.out.println("Current character: Ranger");
    }
}

class GameContext {
    private Character character;

    public GameContext(Character character) {
        this.character = character;
    }

    public void setCharacter(Character newCharacter) {
        this.character = newCharacter;
    }

    public void move() {
        character.walk();
        character.fly();
    }

    public void attack() {
        character.meleeAttack();
        character.castSpell();
    }

    public void display() {
        character.displayRole();
    }
}

