interface NetworkDevice {
    void connect();
    void transferData(String data);
    void runDiagnostics();
    void updateFirmware(String version);
    String getDeviceName();
    void displayInfo();
}

interface MultimediaDevice {
    void powerOn();
    void play();
    void stop();
    void setVolume(int level);
    void setScreenResolution(String resolution);
    String getDeviceName();
    void displayInfo();
}

class Router implements NetworkDevice {
    private String name;
    private String ipAddress;

    public Router(String name, String ipAddress) {
        this.name = name;
        this.ipAddress = ipAddress;
    }

    @Override
    public void connect() {
        System.out.println("Router " + name + " established connection.");
    }

    @Override
    public void transferData(String data) {
        System.out.println("Router transferring data: " + data);
    }

    @Override
    public void runDiagnostics() {
        System.out.println("Running diagnostics on router " + name);
    }

    @Override
    public void updateFirmware(String version) {
        System.out.println("Router firmware updated to version: " + version);
    }

    @Override
    public String getDeviceName() {
        return name;
    }

    @Override
    public void displayInfo() {
        System.out.println("Router: " + name + ", IP: " + ipAddress);
    }
}

class NetworkSwitch implements NetworkDevice {
    private String name;
    private String ipAddress;

    public NetworkSwitch(String name, String ipAddress) {
        this.name = name;
        this.ipAddress = ipAddress;
    }

    @Override
    public void connect() {
        System.out.println("Switch " + name + " established connection.");
    }

    @Override
    public void transferData(String data) {
        System.out.println("Switch transferring data: " + data);
    }

    @Override
    public void runDiagnostics() {
        System.out.println("Running diagnostics on switch " + name);
    }

    @Override
    public void updateFirmware(String version) {
        System.out.println("Switch firmware updated to version: " + version);
    }

    @Override
    public String getDeviceName() {
        return name;
    }

    @Override
    public void displayInfo() {
        System.out.println("Network Switch: " + name + ", IP: " + ipAddress);
    }
}

class SmartTV implements MultimediaDevice {
    private String name;
    private String location;

    public SmartTV(String name, String location) {
        this.name = name;
        this.location = location;
    }

    @Override
    public void powerOn() {
        System.out.println(name + " TV powered on.");
    }

    @Override
    public void play() {
        System.out.println(name + " TV playing content.");
    }

    @Override
    public void stop() {
        System.out.println(name + " TV stopped playback.");
    }

    @Override
    public void setVolume(int level) {
        System.out.println(name + " TV volume set to " + level);
    }

    @Override
    public void setScreenResolution(String resolution) {
        System.out.println(name + " TV resolution set to " + resolution);
    }

    @Override
    public String getDeviceName() {
        return name;
    }

    @Override
    public void displayInfo() {
        System.out.println("Smart TV: " + name + ", Location: " + location);
    }
}

class SmartSpeaker implements MultimediaDevice {
    private String name;
    private String location;

    public SmartSpeaker(String name, String location) {
        this.name = name;
        this.location = location;
    }

    @Override
    public void powerOn() {
        System.out.println(name + " speaker powered on.");
    }

    @Override
    public void play() {
        System.out.println(name + " speaker playing audio.");
    }

    @Override
    public void stop() {
        System.out.println(name + " speaker stopped playback.");
    }

    @Override
    public void setVolume(int level) {
        System.out.println(name + " speaker volume set to " + level);
    }

    @Override
    public void setScreenResolution(String resolution) {
        System.out.println("ERROR: " + name + " speaker doesn't have a screen!");
    }

    @Override
    public String getDeviceName() {
        return name;
    }

    @Override
    public void displayInfo() {
        System.out.println("Smart Speaker: " + name + ", Location: " + location);
    }
}

class DeviceManager {
    public void configureDevice(Object device) {
        if (device instanceof NetworkDevice) {
            NetworkDevice networkDevice = (NetworkDevice) device;
            networkDevice.displayInfo();
            networkDevice.connect();
        } else if (device instanceof MultimediaDevice) {
            MultimediaDevice mediaDevice = (MultimediaDevice) device;
            mediaDevice.displayInfo();
            mediaDevice.powerOn();
        }
    }

    public void updateFirmware(NetworkDevice device) {
        System.out.println("Updating firmware for " + device.getDeviceName());
        device.updateFirmware("v2.4.1");
    }

    public void playMedia(MultimediaDevice device) {
        System.out.println("Playing media on " + device.getDeviceName());
        device.play();
    }

    public void adjustVolume(MultimediaDevice device, int level) {
        System.out.println("Adjusting volume on " + device.getDeviceName());
        device.setVolume(level);
    }


    public void playMedia(NetworkDevice device) {
        System.out.println("Attempting to play media on " + device.getDeviceName());
        System.out.println("ERROR: Network devices cannot play media!");
    }

    public void updateFirmware(MultimediaDevice device) {
        System.out.println("Attempting to update firmware on " + device.getDeviceName());
        System.out.println("ERROR: Cannot update firmware on multimedia devices!");
    }
}

