input:
interface DeviceControl {
    fun turnOn()
    fun turnOff()
    fun volumeUp()
    fun volumeDown()
    fun lock()
}

open class LightBulb {
    protected var isOn: Boolean = false

    open fun turnOn() {
        isOn = true
        println("LightBulb turned on.")
    }

    open fun turnOff() {
        isOn = false
        println("LightBulb turned off.")
    }
}

class RegularLightBulb : LightBulb(), DeviceControl {
    override fun volumeUp() {
        println("Volume up not supported on RegularLightBulb.")
    }

    override fun volumeDown() {
        println("Volume down not supported on RegularLightBulb.")
    }

    override fun lock() {
        println("Lock not supported on RegularLightBulb.")
    }
}

class RGBLightBulb : LightBulb(), DeviceControl {
    private var color: String = "White"

    fun changeColor(newColor: String) {
        color = newColor
        println("RGBLightBulb color changed to $color")
    }

    override fun volumeUp() {
        println("Volume up not supported on RGBLightBulb.")
    }

    override fun volumeDown() {
        println("Volume down not supported on RGBLightBulb.")
    }

    override fun lock() {
        println("Lock not supported on RGBLightBulb.")
    }
}

class TV : DeviceControl {
    private var volume: Int = 10
    private var isOn: Boolean = false

    override fun turnOn() {
        isOn = true
        println("TV turned on.")
    }

    override fun turnOff() {
        isOn = false
        println("TV turned off.")
    }

    override fun volumeUp() {
        volume++
        println("TV volume increased to $volume")
    }

    override fun volumeDown() {
        volume--
        println("TV volume decreased to $volume")
    }

    override fun lock() {
        println("TV locked (parental control enabled).")
    }
}

class Speaker : DeviceControl {
    private var volume: Int = 5
    private var isOn: Boolean = false

    override fun turnOn() {
        isOn = true
        println("Speaker turned on.")
    }

    override fun turnOff() {
        isOn = false
        println("Speaker turned off.")
    }

    override fun volumeUp() {
        volume++
        println("Speaker volume increased to $volume")
    }

    override fun volumeDown() {
        volume--
        println("Speaker volume decreased to $volume")
    }

    override fun lock() {
        println("Speaker locked.")
    }
}

class SmartLock : DeviceControl {
    private var isLocked: Boolean = false

    override fun turnOn() {
        println("SmartLock doesn't need to be turned on.")
    }

    override fun turnOff() {
        println("SmartLock doesn't need to be turned off.")
    }

    override fun volumeUp() {
        println("Volume control not supported on SmartLock.")
    }

    override fun volumeDown() {
        println("Volume control not supported on SmartLock.")
    }

    override fun lock() {
        isLocked = true
        println("SmartLock is now locked.")
    }
}

