    fun calculatePerimeter(): Double
    fun calculateVolume(): Double
}

class Circle(private val radius: Double) : Shape {
    override fun calculateArea(): Double {
        return Math.PI * radius * radius
    }

    override fun calculatePerimeter(): Double {
        return 2 * Math.PI * radius
    }

    override fun calculateVolume(): Double {
        println("Warning: Calculating volume for a 2D shape doesn't make sense!")
        return 0.0
    }
}

class Rectangle(private val width: Double, private val height: Double) : Shape {
    override fun calculateArea(): Double {
        return width * height
    }

    override fun calculatePerimeter(): Double {
        return 2 * (width + height)
    }

    override fun calculateVolume(): Double {
        println("Warning: Calculating volume for a 2D shape doesn't make sense!")
        return 0.0
    }
}

class Line(private val length: Double) : Shape {
    override fun calculateArea(): Double {
        println("Warning: Calculating area for a 1D shape doesn't make sense!")
        return 0.0
    }

    override fun calculatePerimeter(): Double {
        return length
    }

    override fun calculateVolume(): Double {
        println("Warning: Calculating volume for a 1D shape doesn't make sense!")
        return 0.0
    }
}

class Cube(private val side: Double) : Shape {
    override fun calculateArea(): Double {
        return 6 * side * side
    }

    override fun calculatePerimeter(): Double {
        return 12 * side
    }

    override fun calculateVolume(): Double {
        return side * side * side
    }
}

