public interface DeviceControl
{
    void TurnOn();
    void TurnOff();
    void VolumeUp();
    void VolumeDown();
    void Lock();
}

public class LightBulb
{
    protected bool isOn;

    public virtual void TurnOn()
    {
        isOn = true;
        Console.WriteLine("LightBulb turned on.");
    }

    public virtual void TurnOff()
    {
        isOn = false;
        Console.WriteLine("LightBulb turned off.");
    }
}

public class RegularLightBulb : LightBulb, DeviceControl
{
    public void VolumeUp()
    {
        Console.WriteLine("Volume up not supported on RegularLightBulb.");
    }

    public void VolumeDown()
    {
        Console.WriteLine("Volume down not supported on RegularLightBulb.");
    }

    public void Lock()
    {
        Console.WriteLine("Lock not supported on RegularLightBulb.");
    }
}

public class RGBLightBulb : LightBulb, DeviceControl
{
    private string color = "White";

    public void ChangeColor(string newColor)
    {
        color = newColor;
        Console.WriteLine("RGBLightBulb color changed to " + color);
    }

    public void VolumeUp()
    {
        Console.WriteLine("Volume up not supported on RGBLightBulb.");
    }

    public void VolumeDown()
    {
        Console.WriteLine("Volume down not supported on RGBLightBulb.");
    }

    public void Lock()
    {
        Console.WriteLine("Lock not supported on RGBLightBulb.");
    }
}

public class TV : DeviceControl
{
    private int volume = 10;
    private bool isOn = false;

    public void TurnOn()
    {
        isOn = true;
        Console.WriteLine("TV turned on.");
    }

    public void TurnOff()
    {
        isOn = false;
        Console.WriteLine("TV turned off.");
    }

    public void VolumeUp()
    {
        volume++;
        Console.WriteLine("TV volume increased to " + volume);
    }

    public void VolumeDown()
    {
        volume--;
        Console.WriteLine("TV volume decreased to " + volume);
    }

    public void Lock()
    {
        Console.WriteLine("TV locked (parental control enabled).");
    }
}

public class Speaker : DeviceControl
{
    private int volume = 5;
    private bool isOn = false;

    public void TurnOn()
    {
        isOn = true;
        Console.WriteLine("Speaker turned on.");
    }

    public void TurnOff()
    {
        isOn = false;
        Console.WriteLine("Speaker turned off.");
    }

    public void VolumeUp()
    {
        volume++;
        Console.WriteLine("Speaker volume increased to " + volume);
    }

    public void VolumeDown()
    {
        volume--;
        Console.WriteLine("Speaker volume decreased to " + volume);
    }

    public void Lock()
    {
        Console.WriteLine("Speaker locked.");
    }
}

