{
    private bool isLocked = false;

    public void TurnOn()
    {
        Console.WriteLine("SmartLock doesn't need to be turned on.");
    }

    public void TurnOff()
    {
        Console.WriteLine("SmartLock doesn't need to be turned off.");
    }

    public void VolumeUp()
    {
        Console.WriteLine("Volume control not supported on SmartLock.");
    }

    public void VolumeDown()
    {
        Console.WriteLine("Volume control not supported on SmartLock.");
    }

