using System;

public interface IShape
{
    double CalculateArea();
    double CalculatePerimeter();
    double CalculateVolume();
}

public class Circle : IShape
{
    private double radius;

    public Circle(double radius)
    {
        this.radius = radius;
    }

    public double CalculateArea()
    {
        return Math.PI * radius * radius;
    }

    public double CalculatePerimeter()
    {
        return 2 * Math.PI * radius;
    }

    public double CalculateVolume()
    {
        Console.WriteLine("Warning: Calculating volume for a 2D shape doesn't make sense!");
        return 0.0;
    }
}

public class Rectangle : IShape
{
    private double width;
    private double height;

    public Rectangle(double width, double height)
    {
        this.width = width;
        this.height = height;
    }

    public double CalculateArea()
    {
        return width * height;
    }

    public double CalculatePerimeter()
    {
        return 2 * (width + height);
    }

    public double CalculateVolume()
    {
        Console.WriteLine("Warning: Calculating volume for a 2D shape doesn't make sense!");
        return 0.0;
    }
}

public class Line : IShape
{
    private double length;

    public Line(double length)
    {
        this.length = length;
    }

    public double CalculateArea()
    {
        Console.WriteLine("Warning: Calculating area for a 1D shape doesn't make sense!");
        return 0.0;
    }

    public double CalculatePerimeter()
    {
        return length;
    }

    public double CalculateVolume()
    {
        Console.WriteLine("Warning: Calculating volume for a 1D shape doesn't make sense!");
        return 0.0;
    }
}

public class Cube : IShape
{
    private double side;

    public Cube(double side)
    {
        this.side = side;
    }

    public double CalculateArea()
    {
        return 6 * side * side;
    }

    public double CalculatePerimeter()
    {
        return 12 * side;
    }

    public double CalculateVolume()
    {
        return side * side * side;
    }
}

