using System;

interface NetworkDevice
{
    void Connect();
    void TransferData(string data);
    void RunDiagnostics();
    void UpdateFirmware(string version);
    string GetDeviceName();
    void DisplayInfo();
}

interface MultimediaDevice
{
    void PowerOn();
    void Play();
    void Stop();
    void SetVolume(int level);
    void SetScreenResolution(string resolution);
    string GetDeviceName();
    void DisplayInfo();
}

class Router : NetworkDevice
{
    private string name;
    private string ipAddress;

    public Router(string name, string ipAddress)
    {
        this.name = name;
        this.ipAddress = ipAddress;
    }

    public void Connect()
    {
        Console.WriteLine("Router " + name + " established connection.");
    }

    public void TransferData(string data)
    {
        Console.WriteLine("Router transferring data: " + data);
    }

    public void RunDiagnostics()
    {
        Console.WriteLine("Running diagnostics on router " + name);
    }

    public void UpdateFirmware(string version)
    {
        Console.WriteLine("Router firmware updated to version: " + version);
    }

    public string GetDeviceName()
    {
        return name;
    }

    public void DisplayInfo()
    {
        Console.WriteLine("Router: " + name + ", IP: " + ipAddress);
    }
}

class NetworkSwitch : NetworkDevice
{
    private string name;
    private string ipAddress;

    public NetworkSwitch(string name, string ipAddress)
    {
        this.name = name;
        this.ipAddress = ipAddress;
    }

    public void Connect()
    {
        Console.WriteLine("Switch " + name + " established connection.");
    }

    public void TransferData(string data)
    {
        Console.WriteLine("Switch transferring data: " + data);
    }

    public void RunDiagnostics()
    {
        Console.WriteLine("Running diagnostics on switch " + name);
    }

    public void UpdateFirmware(string version)
    {
        Console.WriteLine("Switch firmware updated to version: " + version);
    }

    public string GetDeviceName()
    {
        return name;
    }

    public void DisplayInfo()
    {
        Console.WriteLine("Network Switch: " + name + ", IP: " + ipAddress);
    }
}

class SmartTV : MultimediaDevice
{
    private string name;
    private string location;

    public SmartTV(string name, string location)
    {
        this.name = name;
        this.location = location;
    }

    public void PowerOn()
    {
        Console.WriteLine(name + " TV powered on.");
    }

    public void Play()
    {
        Console.WriteLine(name + " TV playing content.");
    }

    public void Stop()
    {
        Console.WriteLine(name + " TV stopped playback.");
    }

    public void SetVolume(int level)
    {
        Console.WriteLine(name + " TV volume set to " + level);
    }

    public void SetScreenResolution(string resolution)
    {
        Console.WriteLine(name + " TV resolution set to " + resolution);
    }

    public string GetDeviceName()
    {
        return name;
    }

    public void DisplayInfo()
    {
        Console.WriteLine("Smart TV: " + name + ", Location: " + location);
    }
}

class SmartSpeaker : MultimediaDevice
{
    private string name;
    private string location;

    public SmartSpeaker(string name, string location)
    {
        this.name = name;
        this.location = location;
    }

    public void PowerOn()
    {
        Console.WriteLine(name + " speaker powered on.");
    }

    public void Play()
    {
        Console.WriteLine(name + " speaker playing audio.");
    }

    public void Stop()
    {
        Console.WriteLine(name + " speaker stopped playback.");
    }

    public void SetVolume(int level)
    {
        Console.WriteLine(name + " speaker volume set to " + level);
    }

    public void SetScreenResolution(string resolution)
    {
        Console.WriteLine("ERROR: " + name + " speaker doesn't have a screen!");
    }

    public string GetDeviceName()
    {
        return name;
    }

    public void DisplayInfo()
    {
        Console.WriteLine("Smart Speaker: " + name + ", Location: " + location);
    }
}

class DeviceManager
{
    public void ConfigureDevice(object device)
    {
        if (device is NetworkDevice networkDevice)
        {
            networkDevice.DisplayInfo();
            networkDevice.Connect();
        }
        else if (device is MultimediaDevice mediaDevice)
        {
            mediaDevice.DisplayInfo();
            mediaDevice.PowerOn();
        }
    }

    public void UpdateFirmware(NetworkDevice device)
    {
        Console.WriteLine("Updating firmware for " + device.GetDeviceName());
        device.UpdateFirmware("v2.4.1");
    }

    public void PlayMedia(MultimediaDevice device)
    {
        Console.WriteLine("Playing media on " + device.GetDeviceName());
        device.Play();
    }

    public void AdjustVolume(MultimediaDevice device, int level)
    {
        Console.WriteLine("Adjusting volume on " + device.GetDeviceName());
        device.SetVolume(level);
    }

    public void PlayMedia(NetworkDevice device)
    {
        Console.WriteLine("Attempting to play media on " + device.GetDeviceName());
        Console.WriteLine("ERROR: Network devices cannot play media!");
    }

    public void UpdateFirmware(MultimediaDevice device)
    {
        Console.WriteLine("Attempting to update firmware on " + device.GetDeviceName());
        Console.WriteLine("ERROR: Cannot update firmware on multimedia devices!");
    }
}

