        throw new NotSupportedException("Admin system doesn't create bookings directly");
    }

    public void CancelBooking(string bookingId)
    {
        throw new NotSupportedException("Admin system doesn't cancel bookings directly");
    }

    public Booking GetBookingDetails(string bookingId)
    {
        throw new NotSupportedException("Admin system doesn't handle booking details");
    }

