    {
        throw new NotSupportedException("Reporting service doesn't send emails");
    }

    public void SendCancellationNotice(string bookingId, string email)
    {
        throw new NotSupportedException("Reporting service doesn't send emails");
    }

    public byte[] GenerateBookingReport(string bookingId)
    {
        Console.WriteLine("Generating booking report for " + bookingId);
        return new byte[512]; 
    }

