import java.util.Map;

class DataProcessor {
    protected Map<String, Object> data = null;

    public DataProcessor() {
        System.out.println("DataProcessor created.");
    }


    public boolean loadData(String source) {
        System.out.println("Base: Loading data from " + source + "...");

        try { Thread.sleep(100); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }
        if ("valid_source".equals(source)) {
            data = new HashMap<>();
            data.put("key1", "value1");
            data.put("key2", 42);
            System.out.println("Base: Data loaded successfully.");
            return true;
        } else {
            data = null;
            System.out.println("Base: Failed to load data.");
            return false;
        }
    }


