        System.out.println("Base: Processing data...");
        if (data == null) {
            System.out.println("Base: No data to process.");
            return false;
        }

        try { Thread.sleep(200); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }
        data.put("processed", true);
        System.out.println("Base: Data processed.");
        return true;
    }


    public boolean saveResults(String destination) {
        System.out.println("Base: Saving results to " + destination + "...");
        if (data == null || !Boolean.TRUE.equals(data.get("processed"))) {
            System.out.println("Base: No processed data to save.");
            return false;
        }

        try { Thread.sleep(300); } catch (InterruptedException e) { Thread.currentThread().interrupt(); }

        System.out.println("Base: Results saved to " + destination + ".");
        return true;
    }
}

