import java.io.File

open class FileProcessor {
    open fun getStatus(filePath: String): String {
        val file = File(filePath)
        return if (file.exists()) {
            println("Base: Checking status for $filePath - Found.")
            "Ready for processing"
        }
        else {
            println("Base: Checking status for $filePath - Not Found.")
            "File not found"
        }
    }

    open fun processFile(filePath: String) {
        println("Base: Processing file $filePath...")
        val file = File(filePath)
        if (!file.exists()) {
            println("Base: Error - File $filePath not found for processing.")
            return
        }
        println("Base: File $filePath processed successfully.")
    }
}

class ReadOnlyFileProcessor : FileProcessor() {
    override fun getStatus(filePath: String): String {
        println("ReadOnly: Checking status for $filePath - Read-only access.")
        val file = File(filePath)
        return if (file.exists()) {
            "Available (Read-only)"
        }
        else {
            "File not found (Read-only)"
        }
    }

    override fun processFile(filePath: String) {
        println("ReadOnly: Attempted to process file $filePath.")
        throw UnsupportedOperationException("Cannot process (write to) a file using a ReadOnlyFileProcessor.")
    }
}

