using System;
using System.IO;

class FileProcessor
{
    public virtual string GetStatus(string filePath)
    {
        if (File.Exists(filePath))
        {
            Console.WriteLine($"Base: Checking status for {filePath} - Found.");
            return "Ready for processing";
        }
        else
        {
            Console.WriteLine($"Base: Checking status for {filePath} - Not Found.");
            return "File not found";
        }
    }

    public virtual void ProcessFile(string filePath)
    {
        Console.WriteLine($"Base: Processing file {filePath}...");
        if (!File.Exists(filePath))
        {
            Console.WriteLine($"Base: Error - File {filePath} not found for processing.");
            return;
        }
        Console.WriteLine($"Base: File {filePath} processed successfully.");
    }
}

class ReadOnlyFileProcessor : FileProcessor
{
    public override string GetStatus(string filePath)
    {
        Console.WriteLine($"ReadOnly: Checking status for {filePath} - Read-only access.");
        if (File.Exists(filePath))
        {
            return "Available (Read-only)";
        }
        else
        {
            return "File not found (Read-only)";
        }
    }

    public override void ProcessFile(string filePath)
    {
        Console.WriteLine($"ReadOnly: Attempted to process file {filePath}.");
        throw new InvalidOperationException("Cannot process (write to) a file using a ReadOnlyFileProcessor.");
    }
}

