using System;

class Account
{
    public decimal Balance { get; protected set; } = 1000m;

    public virtual void Withdraw(decimal amount)
    {
        if (amount <= 0) return;

        if (Balance >= amount)
        {
            Balance -= amount;
            Console.WriteLine($"Base: Successfully withdrew {amount}. New Balance: {Balance}");
        }
        else
        {
            Console.WriteLine($"Base: Insufficient funds. Cannot withdraw {amount}. Balance: {Balance}");
        }
    }
}

class LimitedWithdrawalAccount : Account
{
    private const decimal MaxWithdrawalLimit = 50m;

    public override void Withdraw(decimal amount)
    {
        if (amount <= 0) return;

        if (amount <= MaxWithdrawalLimit)
        {
            if (Balance >= amount)
            {
                Balance -= amount;
                Console.WriteLine($"Limited (Small): Successfully withdrew {amount}. New Balance: {Balance}");
            }
            else
            {
                 Console.WriteLine($"Limited (Small): Insufficient funds for withdrawal ({amount}). Balance: {Balance}");
            }
        }
        else
        {
            Console.WriteLine($"Limited (Large): Withdrawal request ({amount}) exceeds per-transaction limit ({MaxWithdrawalLimit}). No action taken. Balance remains: {Balance}");
        }
    }
}

