solution_code:
public class ReportGenerator {
    public String generateReport(String reportType, SalesData data) {
        StringBuilder report = new StringBuilder();

        if (reportType.equals("daily")) {
            report.append("DAILY SALES REPORT\n");
            report.append("===================\n\n");

            Map<String, Double> dailySales = new HashMap<>();
            SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");

            for (Sale sale : data.getSales()) {
                String dateStr = dateFormat.format(sale.getDate());
                dailySales.put(dateStr, dailySales.getOrDefault(dateStr, 0.0) + sale.getAmount());
            }

            for (Map.Entry<String, Double> entry : dailySales.entrySet()) {
                report.append("Date: ").append(entry.getKey()).append(", Total: $").append(entry.getValue()).append("\n");
            }

            double total = dailySales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("\nTotal Sales: $").append(total);

        } else if (reportType.equals("product")) {
            report.append("PRODUCT SALES REPORT\n");
            report.append("====================\n\n");

            Map<String, Double> productSales = new HashMap<>();

            for (Sale sale : data.getSales()) {
                productSales.put(sale.getProductName(), productSales.getOrDefault(sale.getProductName(), 0.0) + sale.getAmount());
            }

            for (Map.Entry<String, Double> entry : productSales.entrySet()) {
                report.append("Product: ").append(entry.getKey()).append(", Total: $").append(entry.getValue()).append("\n");
            }

            double total = productSales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("\nTotal Sales: $").append(total);

        } else if (reportType.equals("regional")) {
            report.append("REGIONAL SALES REPORT\n");
            report.append("=====================\n\n");

            Map<String, Double> regionalSales = new HashMap<>();

            for (Sale sale : data.getSales()) {
                regionalSales.put(sale.getRegion(), regionalSales.getOrDefault(sale.getRegion(), 0.0) + sale.getAmount());
            }

            for (Map.Entry<String, Double> entry : regionalSales.entrySet()) {
                report.append("Region: ").append(entry.getKey()).append(", Total: $").append(entry.getValue()).append("\n");
            }

            double total = regionalSales.values().stream().mapToDouble(Double::doubleValue).sum();
            report.append("\nTotal Sales: $").append(total);
        }

        return report.toString();
    }
}

