        Map<String, Double> rates = new HashMap<>();
        rates.put("DE", 0.19);
        rates.put("FR", 0.20);
        rates.put("IT", 0.22);
        return rates;
    }

    private Map<String, Double> getProvinceTaxRates() {
        Map<String, Double> rates = new HashMap<>();
        rates.put("ON", 0.08);
        rates.put("BC", 0.07);
        rates.put("AB", 0.0);
        return rates;
    }
}

