        val report = dataSource.getReport(reportId)

        if (report == null) {
            return ExportResult(false, "Report not found", null)
        }

        val reportData = generateReportData(report, exportFormat)
        if (reportData == null) {
            return ExportResult(false, "Failed to generate report", null)
        }

        val fileName = report.getTitle() + getFileExtension(exportFormat)
        fileSystem.writeFile("$destination/$fileName", reportData)
        return ExportResult(true, "Report exported successfully", "$destination/$fileName")
