        rates["DE"] = 0.19
        rates["FR"] = 0.20
        rates["IT"] = 0.22
        return rates
    }

    private fun getProvinceTaxRates(): Map<String, Double> {
        val rates = HashMap<String, Double>()
        rates["ON"] = 0.08
        rates["BC"] = 0.07
        rates["AB"] = 0.0
        return rates
    }
