using System.Text;

public class SalesData {
    private List<Sale> sales;

    public SalesData(List<Sale> sales) {
        this.sales = sales;
    }

    public List<Sale> GetSales() {
        return sales;
    }
}

public class Sale {
    private string productName;
    private double amount;
    private DateTime date;
    private string region;

    public Sale(string productName, double amount, DateTime date, string region) {
        this.productName = productName;
        this.amount = amount;
        this.date = date;
        this.region = region;
    }

    public string GetProductName() {
        return productName;
    }

    public double GetAmount() {
        return amount;
    }

    public DateTime GetDate() {
        return date;
    }

    public string GetRegion() {
        return region;
    }
}

public class ReportGenerator {
    public string GenerateReport(string reportType, SalesData data) {
        StringBuilder report = new StringBuilder();

        if (reportType == "daily") {
            report.AppendLine("DAILY SALES REPORT");
            report.AppendLine("===================");
            report.AppendLine();

            Dictionary<string, double> dailySales = new Dictionary<string, double>();

            foreach (Sale sale in data.GetSales()) {
                string dateStr = sale.GetDate().ToString("yyyy-MM-dd");
                if (!dailySales.ContainsKey(dateStr)) {
                    dailySales[dateStr] = 0;
                }
                dailySales[dateStr] += sale.GetAmount();
            }

            foreach (var entry in dailySales) {
                report.AppendLine($"Date: {entry.Key}, Total: ${entry.Value}");
            }

            double total = dailySales.Values.Sum();
            report.AppendLine();
            report.Append($"Total Sales: ${total}");

        } else if (reportType == "product") {
            report.AppendLine("PRODUCT SALES REPORT");
            report.AppendLine("====================");
            report.AppendLine();

            Dictionary<string, double> productSales = new Dictionary<string, double>();

            foreach (Sale sale in data.GetSales()) {
                if (!productSales.ContainsKey(sale.GetProductName())) {
                    productSales[sale.GetProductName()] = 0;
                }
                productSales[sale.GetProductName()] += sale.GetAmount();
            }

            foreach (var entry in productSales) {
                report.AppendLine($"Product: {entry.Key}, Total: ${entry.Value}");
            }

            double total = productSales.Values.Sum();
            report.AppendLine();
            report.Append($"Total Sales: ${total}");

        } else if (reportType == "regional") {
            report.AppendLine("REGIONAL SALES REPORT");
            report.AppendLine("=====================");
            report.AppendLine();

            Dictionary<string, double> regionalSales = new Dictionary<string, double>();

            foreach (Sale sale in data.GetSales()) {
                if (!regionalSales.ContainsKey(sale.GetRegion())) {
                    regionalSales[sale.GetRegion()] = 0;
                }
                regionalSales[sale.GetRegion()] += sale.GetAmount();
            }

            foreach (var entry in regionalSales) {
                report.AppendLine($"Region: {entry.Key}, Total: ${entry.Value}");
            }

            double total = regionalSales.Values.Sum();
            report.AppendLine();
            report.Append($"Total Sales: ${total}");
        }

        return report.ToString();
    }
}

