    public String filterContent(Content content) {
        String filteredText = content.getText();

        if (content.getType().equals("email")) {
            filteredText = filterEmailContent(filteredText, content.getMetadata());

        } else if (content.getType().equals("social")) {
            filteredText = filterSocialContent(filteredText, content.getMetadata());

        } else if (content.getType().equals("comment")) {
            filteredText = filterCommentContent(filteredText, content.getMetadata());
        }

        filteredText = removeCommonProfanity(filteredText);
        filteredText = removePersonalInfo(filteredText);

        return filteredText;
    }

    private String filterEmailContent(String text, Map<String, String> metadata) {
        String audience = metadata.getOrDefault("audience", "general");

        if (audience.equals("public")) {
            text = text.replaceAll("(?i)confidential:", "[REDACTED]");
            text = text.replaceAll("(?i)internal use only", "[INTERNAL]");
        }

        text = text.replaceAll("(?i)from:\s.*?\n", "");
        text = text.replaceAll("(?i)to:\s.*?\n", "");
        text = text.replaceAll("(?i)cc:\s.*?\n", "");

        return text;
    }

    private String filterSocialContent(String text, Map<String, String> metadata) {
        String platform = metadata.getOrDefault("platform", "general");
        String audience = metadata.getOrDefault("audience", "general");

        if (platform.equals("twitter")) {
            text = text.replaceAll("@([A-Za-z0-9_]+)", "[USERNAME]");
        } else if (platform.equals("facebook")) {
            text = text.replaceAll("@\[[^\]]+\]", "[NAME]");
        }

        if (audience.equals("minor")) {
            text = text.replaceAll("(?i)alcohol|beer|wine", "[AGE-RESTRICTED]");
        }

        return text;
    }

    private String filterCommentContent(String text, Map<String, String> metadata) {
        String context = metadata.getOrDefault("context", "general");

        if (context.equals("news")) {
            text = text.replaceAll("(?i)allegedly|reportedly", "[STATEMENT]");
        } else if (context.equals("review")) {
            text = text.replaceAll("(?i)terrible|awful|worst ever", "not satisfactory");
        }

        return text;
    }

    private String removeCommonProfanity(String text) {
        text = text.replaceAll("(?i)badword1|badword2|badword3", "[INAPPROPRIATE]");
        return text;
    }

    private String removePersonalInfo(String text) {
        text = text.replaceAll("\d{3}-\d{2}-\d{4}", "[SSN]");
        text = text.replaceAll("\d{16}", "[CREDIT_CARD]");
        return text;
    }
}

