public class PaymentProcessor {
    public void processPayment(String paymentType, double amount) {
        switch (paymentType) {
            case "creditCard":
                processCreditCardPayment(amount);
                break;
            case "paypal":
                processPaypalPayment(amount);
                break;
            case "bankTransfer":
                processBankTransferPayment(amount);
                break;
            default:
                throw new IllegalArgumentException("Unsupported payment type");
        }
    }

    private void processCreditCardPayment(double amount) {
        System.out.println("Processing credit card payment of $" + amount);
        System.out.println("Validating credit card information");
        System.out.println("Charging credit card");
        System.out.println("Recording the credit card transaction");
    }

    private void processPaypalPayment(double amount) {
        System.out.println("Processing PayPal payment of $" + amount);
        System.out.println("Redirecting user to PayPal");
        System.out.println("Confirming PayPal payment");
        System.out.println("Recording the PayPal transaction");
    }

    private void processBankTransferPayment(double amount) {
        System.out.println("Processing bank transfer of $" + amount);
        System.out.println("Generating bank transfer instructions");
        System.out.println("Sending transfer confirmation email");
        System.out.println("Recording the bank transfer transaction");
    }
}

