        self.inventory_service = inventory_service
        self.logger = logger

    def process_shipment(self, order_id):
        order = self.order_repository.find_by_id(order_id)

        if order is None:
            raise ValueError(f"Order not found: {order_id}")

        if order.get_status() != OrderStatus.PAID:
            return ShipmentResult(False, "Order is not paid yet", None)

        for item in order.get_items():
            if not self.inventory_service.is_in_stock(item.get_product_id(), item.get_quantity()):
                return ShipmentResult(False, f"Product out of stock: {item.get_product_id()}", None)

        shipment = Shipment(order_id, datetime.datetime.now())
        shipping_cost = self.calculate_shipping_cost(order)
        shipment.set_cost(shipping_cost.get_cost())

        # Reserve inventory items
        for item in order.get_items():
            self.inventory_service.reserve(item.get_product_id(), item.get_quantity())

        # Update order status
        order.set_status(OrderStatus.SHIPPED)
        self.order_repository.update(order)

        self.logger.log(f"Order shipped: {order_id}, cost: {shipping_cost.get_cost()}")
