public class ReportFetcher {
    private DataSource dataSource;

    public ReportFetcher(DataSource dataSource) {
        this.dataSource = dataSource;
    }

    public Report getReport(String reportId) {
        return dataSource.getReport(reportId);
    }
}

public class ReportDataGenerator {
    private DataSource dataSource;

    public ReportDataGenerator(DataSource dataSource) {
        this.dataSource = dataSource;
    }

    public byte[] generateReportData(Report report, String exportFormat) {
        try {
            ReportData data = dataSource.getReportData(report.getId());

            if (exportFormat.equals("pdf")) {
                PdfDocument document = new PdfDocument();
                return document.generate();

            } else if (exportFormat.equals("excel")) {
                ExcelDocument document = new ExcelDocument();
                return document.generate();

            } else if (exportFormat.equals("html")) {
                StringBuilder html = new StringBuilder();
                return html.toString().getBytes();

            } else if (exportFormat.equals("csv")) {
                StringBuilder csv = new StringBuilder();
                return csv.toString().getBytes();
            }

        } catch (Exception e) {
            return null;
        }
        return null;
    }
}

public class ReportExporter {
    private FileSystem fileSystem;

    public ReportExporter(FileSystem fileSystem) {
        this.fileSystem = fileSystem;
    }

    public ExportResult exportReport(Report report, byte[] reportData, String destination) {
        if (report == null || reportData == null) {
            return new ExportResult(false, "Report or data not found", null);
        }

        String fileName = report.getTitle() + getFileExtension(reportData);
        fileSystem.writeFile(destination + "/" + fileName, reportData);
        return new ExportResult(true, "Report exported successfully", destination + "/" + fileName);
    }

    private String getFileExtension(byte[] data) {
        // Assuming the extension is based on the data type
        if (data.length > 0 && data[0] == 255 && data[1] == 254) return ".pdf";
        else if (data.length > 0 && data[0] == 78 && data[1] == 97 && data[2] == 109 && data[3] == 101) return ".xlsx";
        else if (data.length > 0 && data[0] == '<' && data[1] == 'h' && data[2] == 't' && data[3] == 'm' && data[4] == 'l') return ".html";
        else if (data.length > 0 && data[0] == 65 && data[1] == 44 && data[2] == 44) return ".csv";
        else return ".txt";
    }
}

public class ExportResult {
    private boolean success;
    private String message;
    private String outputPath;

    public ExportResult(boolean success, String message, String outputPath) {
        this.success = success;
        this.message = message;
        this.outputPath = outputPath;
    }
}

public interface DataSource {
    Report getReport(String reportId);
    ReportData getReportData(String reportId);
}

public interface FileSystem {
