    }
}

interface Logger {
    fun log(message: String)
}

enum class OrderStatus {
    CREATED, PAID, SHIPPED, DELIVERED, CANCELLED
}

class ShippingService(
    private val orderRepository: OrderRepository,
    private val inventoryService: InventoryService,
    private val logger: Logger
) {

    fun processShipment(orderId: String): ShipmentResult {
        val order = orderRepository.findById(orderId)

        if (order == null) {
            throw IllegalArgumentException("Order not found: $orderId")
        }

        if (order.getStatus() != OrderStatus.PAID) {
            return ShipmentResult(false, "Order is not paid yet", null)
        }

        for (item in order.getItems()) {
            if (!inventoryService.isInStock(item.getProductId(), item.getQuantity())) {
                return ShipmentResult(false, "Product out of stock: ${item.getProductId()}", null)
            }
        }

        val shipment = Shipment(orderId, Date())
        val shippingCost = calculateShippingCost(order)
        shipment.setCost(shippingCost.getCost())

        // Reserve inventory items
        for (item in order.getItems()) {
            inventoryService.reserve(item.getProductId(), item.getQuantity())
        }

        // Update order status
        order.setStatus(OrderStatus.SHIPPED)
        orderRepository.update(order)

        logger.log("Order shipped: $orderId, cost: ${shippingCost.getCost()}")
        return ShipmentResult(true, "Order shipped successfully", shipment)
    }

    private fun calculateShippingCost(order: Order): ShippingCost {
        val destination = order.getShippingAddress()
        val totalWeight = calculateOrderWeight(order)
        var baseCost = 0.0

        val shippingMethod = order.getShippingMethod()

        if (shippingMethod == "standard") {
            // Standard shipping: base cost + weight factor
            baseCost = 5.0
            val weightCost = totalWeight * 0.5
            val distance = calculateDistance(destination)
            val distanceFactor = distance * 0.01

            return ShippingCost(baseCost + weightCost + distanceFactor)

        } else if (shippingMethod == "express") {
            // Express shipping: higher base cost + weight factor
            baseCost = 15.0
            val weightCost = totalWeight * 0.75
            val distance = calculateDistance(destination)
            val distanceFactor = distance * 0.015

            return ShippingCost(baseCost + weightCost + distanceFactor)

        } else if (shippingMethod == "overnight") {
            // Overnight shipping: premium cost
            baseCost = 25.0
            val weightCost = totalWeight * 1.0
            val distance = calculateDistance(destination)
            val distanceFactor = distance * 0.02

            return ShippingCost(baseCost + weightCost + distanceFactor)

        } else if (shippingMethod == "international") {
            // International shipping: customs fees + premium
            baseCost = 30.0
            val weightCost = totalWeight * 2.0
            val distance = calculateDistance(destination)
            val distanceFactor = distance * 0.025
            val customsFee = order.getTotalAmount() * 0.05 // 5% of order value

            return ShippingCost(baseCost + weightCost + distanceFactor + customsFee)
        }

        // Default shipping cost if method not recognized
        return ShippingCost(10.0)
    }

    private fun calculateOrderWeight(order: Order): Double {
        var totalWeight = 0.0
        for (item in order.getItems()) {
            val product = inventoryService.getProduct(item.getProductId())
            totalWeight += product.getWeight() * item.getQuantity()
        }
        return totalWeight
    }

    private fun calculateDistance(destination: Address): Double {
        // Simplified distance calculation. In a real system, this would use
        // geographic coordinates or a distance service.
        val warehouseAddress = Address("Warehouse", "123 Main St", "City", "State", "Country", "12345")

        if (warehouseAddress.getCountry() == destination.getCountry()) {
            if (warehouseAddress.getState() == destination.getState()) {
                if (warehouseAddress.getCity() == destination.getCity()) {
                    return 10.0 // Same city
                }
                return 50.0 // Same state, different city
            }
            return 200.0 // Same country, different state
        }
        return 1000.0 // International
    }
}

