    {
        double totalTax = 0.0;

        if (order.GetCountry().Equals("US"))
        {
            Dictionary<string, double> stateTaxRates = GetStateTaxRates();
            Dictionary<string, double> categoryTaxRates = GetCategoryTaxRates();

            string state = order.GetCustomerId().Substring(0, 2);
            double stateTaxRate = stateTaxRates.ContainsKey(state) ? stateTaxRates[state] : 0.05;

            foreach (OrderItem item in order.GetItems())
            {
                double itemPrice = item.GetPrice() * item.GetQuantity();
                double categoryRate = categoryTaxRates.ContainsKey(item.GetCategory()) ? categoryTaxRates[item.GetCategory()] : 0.0;
                double itemTax = itemPrice * (stateTaxRate + categoryRate);
                totalTax += itemTax;
            }
        }
        else if (order.GetCountry().Equals("EU"))
        {
            Dictionary<string, double> countryVatRates = GetCountryVatRates();

            string countryCode = order.GetCustomerId().Substring(0, 2);
            double vatRate = countryVatRates.ContainsKey(countryCode) ? countryVatRates[countryCode] : 0.20;

            foreach (OrderItem item in order.GetItems())
            {
                double itemPrice = item.GetPrice() * item.GetQuantity();
                totalTax += itemPrice * vatRate;
            }
        }
        else if (order.GetCountry().Equals("CA"))
        {
            Dictionary<string, double> provinceTaxRates = GetProvinceTaxRates();
            double gst = 0.05;

            string province = order.GetCustomerId().Substring(0, 2);
            double provincialTax = provinceTaxRates.ContainsKey(province) ? provinceTaxRates[province] : 0.08;

            foreach (OrderItem item in order.GetItems())
            {
                double itemPrice = item.GetPrice() * item.GetQuantity();
                totalTax += itemPrice * (gst + provincialTax);
            }
        }

        return totalTax;
    }

    private Dictionary<string, double> GetStateTaxRates()
    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("CA", 0.0725);
        rates.Add("NY", 0.045);
        rates.Add("TX", 0.0625);
        return rates;
    }

    private Dictionary<string, double> GetCategoryTaxRates()
    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("luxury", 0.03);
        rates.Add("food", 0.0);
        return rates;
    }

    private Dictionary<string, double> GetCountryVatRates()
    {
        Dictionary<string, double> rates = new Dictionary<string, double>();
        rates.Add("DE", 0.19);
        rates.Add("FR", 0.20);
        rates.Add("IT", 0.22);
        return rates;
    }

