            // International shipping: customs fees + premium
            baseCost = 30.0;
            double weightCost = totalWeight * 2.0;
            double distanceFactor = _distanceCalculator.CalculateDistance(destination, new Address("Warehouse", "123 Main St", "City", "State", "Country", "12345")) * 0.02;
            double customsFee = order.GetTotalValue() * 0.1; // Example custom fee

            return baseCost + weightCost + distanceFactor + customsFee;
        }

        throw new ArgumentException("Invalid shipping method");
    }
}
