import java.util.Date

class Order(
    private val id: String,
    private val items: List<OrderItem>,
    private val shippingAddress: Address,
    private val shippingMethod: String
) {
    private var status: OrderStatus = OrderStatus.CREATED
    private var totalAmount: Double = 0.0

    init {
        calculateTotalAmount()
    }

    private fun calculateTotalAmount() {
        totalAmount = items.sumByDouble { it.getPrice() * it.getQuantity() }
    }

    fun getId(): String {
        return id
    }

    fun getItems(): List<OrderItem> {
        return items
    }

    fun getShippingAddress(): Address {
        return shippingAddress
