            items.add(new InvoiceItem("Widget B", 1, 45.50));

        } catch (SQLException e) {
            System.err.println("[InvoiceProcessor] Database error fetching items: " + e.getMessage());
            return null;
        }
        return items;
    }

    private double calculateTotal(List<InvoiceItem> items) {
        System.out.println("[InvoiceProcessor] Calculating total...");

        double total = 0;
        for (InvoiceItem item : items) {
            total += item.quantity * item.price;
        }
        return total;
    }

    private String generateInvoiceNumber(int orderId) {
        System.out.println("[InvoiceProcessor] Generating invoice number...");

        return "INV-" + orderId + "-" + new Date().getTime();
    }

    private boolean saveInvoiceToDatabase(String invoiceNumber, int orderId, String customerEmail, double totalAmount) {
        System.out.println("[InvoiceProcessor] Saving invoice " + invoiceNumber + " to DB " + dbUrl + "...");
        String sql = "INSERT INTO invoices (invoice_number, order_id, customer_email, total_amount, invoice_date) VALUES (?, ?, ?, ?, CURRENT_DATE)";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.setString(1, invoiceNumber);
            pstmt.setInt(2, orderId);
            pstmt.setString(3, customerEmail);
            pstmt.setDouble(4, totalAmount);
            int affectedRows = pstmt.executeUpdate();
            return affectedRows > 0;
        } catch (SQLException e) {
            System.err.println("[InvoiceProcessor] Database error saving invoice: " + e.getMessage());
            return false;
        }
    }

    private String generatePdfInvoice(String invoiceNumber, String customerEmail, List<InvoiceItem> items, double totalAmount) {
        System.out.println("[InvoiceProcessor] Generating PDF invoice for " + invoiceNumber + " in " + invoiceStoragePath + "...");

        String filePath = invoiceStoragePath + "/" + invoiceNumber + ".pdf";
        try (FileWriter writer = new FileWriter(filePath)) {

            writer.write("Invoice Number: " + invoiceNumber + "\n");
            writer.write("Customer: " + customerEmail + "\n");
            writer.write("Items:\n");
            for (InvoiceItem item : items) {
                writer.write("  " + item.productName + " x" + item.quantity + " @ " + item.price + "\n");
            }
            writer.write("Total: " + totalAmount + "\n");

            return filePath;
        } catch (IOException e) {
            System.err.println("[InvoiceProcessor] Error generating PDF file: " + e.getMessage());
            return null;
        }

    }

    private boolean emailInvoice(String recipientEmail, String invoiceFilePath) {
        System.out.println("[InvoiceProcessor] Emailing invoice " + invoiceFilePath + " to " + recipientEmail + " via SMTP " + smtpHost + "...");

        Properties props = new Properties();
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.host", smtpHost);
        props.put("mail.smtp.port", "587");

        Session session = Session.getInstance(props, new Authenticator() {
            protected PasswordAuthentication getPasswordAuthentication() {
                return new PasswordAuthentication("invoices@example.com", "password"); 
            }
        });

        try {
            Message message = new MimeMessage(session);
            message.setFrom(new InternetAddress("invoices@example.com"));
            message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(recipientEmail));
            message.setSubject("Your Invoice");
            message.setText("Please find your invoice attached.");

            MimeBodyPart messageBodyPart = new MimeBodyPart();
            messageBodyPart.setText("Please find your invoice attached.");

            Multipart multipart = new MimeMultipart();
            multipart.addBodyPart(messageBodyPart);

            MimeBodyPart attachmentPart = new MimeBodyPart();
            attachmentPart.attachFile(invoiceFilePath);
            multipart.addBodyPart(attachmentPart);

            message.setContent(multipart);


