import javax.mail.internet.MimeMessage
import javax.mail.Session
import java.util.Properties

data class UserProfile(var id: String, var name: String, var bio: String?, var profilePicturePath: String?)

class UserProfileManager(private val dbUrl: String, private val profilePictureDir: String, private val smtpHost: String) {

    fun updateProfile(userId: String, name: String?, bio: String?, profilePictureFile: File?): UserProfile? {
        println("[UserProfileManager] Starting profile update for user $userId")

        val existingProfile = fetchProfile(userId)
        if (existingProfile == null) {
            println("[UserProfileManager] User profile not found for $userId.")
            return null
        }
        println("[UserProfileManager] Existing profile fetched.")

        existingProfile.name = name ?: existingProfile.name
        existingProfile.bio = bio ?: existingProfile.bio
        println("[UserProfileManager] Profile data updated in memory.")

        var newProfilePicturePath: String? = existingProfile.profilePicturePath
        if (profilePictureFile != null) {
            if (!validateImage(profilePictureFile)) {
                 println("[UserProfileManager] Invalid profile picture file.")
                 return null
            }
            newProfilePicturePath = saveProfilePicture(userId, profilePictureFile)
            existingProfile.profilePicturePath = newProfilePicturePath
            println("[UserProfileManager] Profile picture processed and saved.")
        }

        if (!saveProfile(existingProfile)) {
            println("[UserProfileManager] Failed to save updated profile.")

            return null
        }
        println("[UserProfileManager] Profile saved to database.")

        notifyUserOfUpdate(existingProfile)
        println("[UserProfileManager] User notified of profile update.")

        println("[UserProfileManager] Profile update complete for user $userId.")
        return existingProfile
    }

    private fun fetchProfile(userId: String): UserProfile? {
        println("[UserProfileManager] Fetching profile for $userId from $dbUrl...")
        var conn: Connection? = null
        try {
            conn = DriverManager.getConnection(dbUrl)

             return UserProfile(userId, "Test User", "Just a test user", "")
        } catch (e: Exception) {
            println("[UserProfileManager] DB Fetch Error: ${e.message}")
            return null
        } finally {
            conn?.close()
        }
    }

    private fun saveProfile(profile: UserProfile): Boolean {
        println("[UserProfileManager] Saving profile for ${profile.id} to $dbUrl...")
        var conn: Connection? = null
        try {
            conn = DriverManager.getConnection(dbUrl)

            return true
        } catch (e: Exception) {
            println("[UserProfileManager] DB Save Error: ${e.message}")
            return false
        } finally {
            conn?.close()
        }
    }

    private fun validateImage(file: File): Boolean {
         println("[UserProfileManager] Validating image file: ${file.name}")

        return file.exists() && file.length() > 0
    }

    private fun saveProfilePicture(userId: String, file: File): String? {
        println("[UserProfileManager] Saving profile picture for $userId to $profilePictureDir...")
        val image = ImageIO.read(file)
        if (image == null) {
             println("[UserProfileManager] Could not read image file.")
             return null
        }

        val resizedImage: Image = image.getScaledInstance(128, 128, Image.SCALE_SMOOTH)
        val outputFileName = "${userId}_profile.jpg"
        val outputFile = File(profilePictureDir, outputFileName)
        try {
             ImageIO.write(resizedImage, "jpg", outputFile)
             return outputFile.absolutePath
        } catch (e: Exception) {
            println("[UserProfileManager] Image Save Error: ${e.message}")
            return null
        }
    }

    private fun notifyUserOfUpdate(profile: UserProfile) {
        println("[UserProfileManager] Notifying user ${profile.id} via $smtpHost...")
        val props = Properties()
        props["mail.smtp.host"] = smtpHost
        val session = Session.getInstance(props)
        val message = MimeMessage(session)
        message.setFrom(InternetAddress("no-reply@app.com"))

        message.setRecipients(MimeMessage.RecipientType.TO, InternetAddress.parse("test@example.com"))
        message.subject = "Your Profile Has Been Updated"
        message.setText("Hello ${profile.name}, your profile information was recently updated.")

    }
}

