    fun updateProfile(userId: String, name: String?, bio: String?, profilePictureFile: File?): UserProfile? {
        println("[UserProfileManager] Starting profile update for user $userId")

        val existingProfile = fetchProfile(userId)
        if (existingProfile == null) {
            println("[UserProfileManager] User profile not found for $userId.")
            return null
        }

        handleUpdateInMemory(name, bio, existingProfile)

        if (profilePictureFile != null) {
            handleProfilePicture(existingProfile, profilePictureFile)
        }

        notifyUserOfUpdate(profile)
    }

    private fun handleUpdateInMemory(name: String?, bio: String?, existingProfile: UserProfile){
        if (validateNameAndBio(name, bio)) {
            existingProfile.name = name ?: ""
            existingProfile.bio = bio ?: ""
        }
    }

    private fun handleProfilePicture(existingProfile: UserProfile, profilePictureFile: File){
        if (validateImage(profilePictureFile)) {
            val outputFileName = saveProfilePicture(existingProfile.id, profilePictureFile)
            existingProfile.picturePath = outputFileName ?: ""
        }
    }

    private fun validateNameAndBio(name: String?, bio: String?): Boolean{
         println("[UserProfileManager] Validating name and bio")

        return (name?.isNotBlank() ?: true) && (bio?.isNotBlank() ?: true)
    }

    // Other methods remain the same.
}

