    {
        Console.WriteLine($"[GenerateShippingLabel] Generating label for order {order.Id} via shipping API at {_shippingApiEndpoint}...");

        return $"ShippingLabel_{order.Id}.pdf";
    }

    private bool UpdateOrderStatus(int orderId, string status)
    {
        Console.WriteLine($"[UpdateOrderStatus] Updating status for order {orderId} to {status} in DB {_dbConnectionString}...");
