enum class FlightStatus {
    SCHEDULED, BOARDING, DEPARTED, LANDED, CANCELLED, DELAYED
}

enum class PaymentMethod {
    CREDIT_CARD, DEBIT_CARD, PAYPAL
}

data class Flight(
    val flightNumber: String,
    val origin: String,
    val destination: String,
    val departureTime: LocalDateTime,
    val arrivalTime: LocalDateTime,
    var status: FlightStatus = FlightStatus.SCHEDULED
)

data class Booking(
    val bookingId: String,
    val passengerId: String,
    val flightNumber: String,
    val bookingTime: LocalDateTime = LocalDateTime.now()
) {
    override fun toString(): String {
        return "Booking(bookingId='$bookingId', passengerId='$passengerId', flightNumber='$flightNumber', bookingTime=${bookingTime.format(DateTimeFormatter.ISO_LOCAL_DATE_TIME)})"
    }
}

