import datetime
from typing import List, Dict

class Sale:
    def __init__(self, product_name: str, amount: float, date: datetime.date, region: str):
        self.product_name = product_name
        self.amount = amount
        self.date = date
        self.region = region

class SalesData:
    def __init__(self, sales: List[Sale]):
        self.sales = sales

    def get_sales(self) -> List[Sale]:
        return self.sales

class ReportGenerator:
    def generate_report(self, report_type: str, data: SalesData) -> str:
        if report_type == "daily":
            return self._generate_daily_report(data)
        elif report_type == "product":
            return self._generate_product_report(data)
        elif report_type == "regional":
            return self._generate_regional_report(data)
        else:
            raise ValueError("Invalid report type")

    def _generate_daily_report(self, data: SalesData) -> str:
        report = ["DAILY SALES REPORT", "===================\n"]
        daily_sales = self._aggregate_sales(data, key=lambda sale: sale.date.strftime("%Y-%m-%d"))
        report.extend(self._format_sales(daily_sales))
        return "\n".join(report)

    def _generate_product_report(self, data: SalesData) -> str:
        report = ["PRODUCT SALES REPORT", "====================\n"]
        product_sales = self._aggregate_sales(data, key=lambda sale: sale.product_name)
        report.extend(self._format_sales(product_sales))
        return "\n".join(report)

    def _generate_regional_report(self, data: SalesData) -> str:
        report = ["REGIONAL SALES REPORT", "====================="]
        regional_sales = self._aggregate_sales(data, key=lambda sale: sale.region)
        report.extend(self._format_sales(regional_sales))
        return "\n".join(report)

    def _aggregate_sales(self, data: SalesData, key) -> Dict[str, float]:
        aggregated = {}
        for sale in data.get_sales():
            key_value = key(sale)
            if key_value in aggregated:
                aggregated[key_value] += sale.amount
            else:
                aggregated[key_value] = sale.amount
        return aggregated

    def _format_sales(self, sales: Dict[str, float]) -> List[str]:
        total = sum(sales.values())
        formatted_sales = [f"{key}: ${amount}" for key, amount in sales.items()]
        formatted_sales.append(f"\nTotal Sales: ${total}")
        return formatted_sales

