interface DocumentRepository {
    fun save(document: Document)
    fun update(document: Document)
    fun findById(id: String): Document?
}

interface SecurityService {
    fun canShareDocument(document: Document, user: User): Boolean
}

interface Logger {
    fun log(message: String)
}

class DocumentService(
    private val repository: DocumentRepository,
    private val securityService: SecurityService,
    private val logger: Logger,
    private val notificationService: NotificationService
) {

    fun createDocument(title: String, content: String, creator: User): Document {
        val document = Document(title, content, creator)
        repository.save(document)
        logger.log("Document created: ${'$'}{document.getId()}")
        return document
    }

    fun shareDocument(document: Document, recipient: User, sharedBy: User) {
        validateSharingPermissions(document, sharedBy)
        document.addSharedUser(recipient)
        repository.update(document)
        logger.log("Document ${'$'}{document.getId()} shared with ${'$'}{recipient.getUsername()}")
        notificationService.notifyUser(document, recipient, sharedBy)
    }

    private fun validateSharingPermissions(document: Document, user: User) {
        if (!securityService.canShareDocument(document, user)) {
            throw SecurityException("User does not have permission to share this document")
        }
    }
}

class NotificationService(private val logger: Logger) {
    fun notifyUser(document: Document, recipient: User, sharedBy: User) {
        val recipientContact = recipient.getContactInfo()
        val message = "Document '${'$'}{document.getTitle()}' has been shared with you by ${'$'}{sharedBy.getUsername()}"

        // Notification logic based on recipient's preferred contact method
        val contactType = recipient.getPreferredContactMethod()

        when (contactType) {
            "email" -> sendEmail(recipientContact, "Document Shared", message)
            "sms" -> sendSMS(recipientContact, message)
            "push" -> sendPushNotification(recipientContact, "Document Shared", message)
            "slack" -> sendSlackMessage(recipientContact, message)
            else -> logger.log("Unknown contact method: ${'$'}{contactType}")
