class Content(
    private val text: String,
    private val type: String,
    private val metadata: Map<String, String>
) {
    fun getText(): String {
        return text
    }

    fun getType(): String {
        return type
    }

    fun getMetadata(): Map<String, String> {
        return metadata
    }
}

class ContentFilter {
    fun filterContent(content: Content): String {
        var filteredText = content.getText()

        if (content.getType() == "email") {
            filteredText = filterEmailContent(filteredText, content.getMetadata())

        } else if (content.getType() == "social") {
            filteredText = filterSocialContent(filteredText, content.getMetadata())

        } else if (content.getType() == "comment") {
            filteredText = filterCommentContent(filteredText, content.getMetadata())
        }

        filteredText = removeCommonProfanity(filteredText)
        filteredText = removePersonalInfo(filteredText)

        return filteredText
    }

    private fun filterEmailContent(text: String, metadata: Map<String, String>): String {
        var result = text
        val audience = metadata.getOrDefault("audience", "general")

        if (audience == "public") {
            result = result.replace(Regex("(?i)confidential:"), "[REDACTED]")
            result = result.replace(Regex("(?i)internal use only"), "[INTERNAL]")
        }

        result = result.replace(Regex("(?i)from:\s.*?\n"), "")
        result = result.replace(Regex("(?i)to:\s.*?\n"), "")
        result = result.replace(Regex("(?i)cc:\s.*?\n"), "")

        return result
    }

    private fun filterSocialContent(text: String, metadata: Map<String, String>): String {
        var result = text
        val platform = metadata.getOrDefault("platform", "general")
        val audience = metadata.getOrDefault("audience", "general")

        if (platform == "twitter") {
            result = result.replace(Regex("@([A-Za-z0-9_]+)"), "[USERNAME]")
        } else if (platform == "facebook") {
            result = result.replace(Regex("@\\[[^\\]]+\\]"), "[NAME]")
        }

        if (audience == "minor") {
            result = result.replace(Regex("(?i)alcohol|beer|wine"), "[AGE-RESTRICTED]")
        }

        return result
    }

    private fun filterCommentContent(text: String, metadata: Map<String, String>): String {
        var result = text
        val context = metadata.getOrDefault("context", "general")

        if (context == "news") {
            result = result.replace(Regex("(?i)allegedly|reportedly"), "[STATEMENT]")
        } else if (context == "review") {
            result = result.replace(Regex("(?i)terrible|awful|worst ever"), "not satisfactory")
        }

        return result
    }

    private fun removeCommonProfanity(text: String): String {
        return text.replace(Regex("(?i)badword1|badword2|badword3"), "[INAPPROPRIATE]")
    }

    private fun removePersonalInfo(text: String): String {
        var result = text
        result = result.replace(Regex("\\d{3}-\\d{2}-\\d{4}"), "[SSN]")
        result = result.replace(Regex("\\d{16}"), "[CREDIT_CARD]")
        return result
    }
}

