            report.AppendLine($"Product: {entry.Key}, Total: ${entry.Value}");
        }

        double total = productSales.Values.Sum();
        report.AppendLine();
        report.Append($"Total Sales: ${total}");

        return report.ToString();
    }
}

public class RegionalReportGenerator : IReportGenerator {
    public string GenerateReport(SalesData data) {
        StringBuilder report = new StringBuilder();
        report.AppendLine("REGIONAL SALES REPORT");
        report.AppendLine("=====================");
        report.AppendLine();

        Dictionary<string, double> regionalSales = new Dictionary<string, double>();

        foreach (Sale sale in data.GetSales()) {
            if (!regionalSales.ContainsKey(sale.GetRegion())) {
                regionalSales[sale.GetRegion()] = 0;
            }
            regionalSales[sale.GetRegion()] += sale.GetAmount();
        }

        foreach (var entry in regionalSales) {
            report.AppendLine($"Region: {entry.Key}, Total: ${entry.Value}");
        }

        double total = regionalSales.Values.Sum();
        report.AppendLine();
        report.Append($"Total Sales: ${total}");

        return report.ToString();
    }
}

