import javax.mail.*;
import javax.mail.internet.*;
import java.util.Properties;

public class SystemEventHandler {

    private final String dbUrl;
    private final String smtpHost;

    public SystemEventHandler(String dbUrl, String smtpHost) {
        this.dbUrl = dbUrl;
        this.smtpHost = smtpHost;
        initializeDb();
    }

    private void initializeDb() {
        System.out.println("[SystemEventHandler] Initializing DB " + dbUrl + "...");
        String sql = "CREATE TABLE IF NOT EXISTS events (id INTEGER PRIMARY KEY AUTOINCREMENT, event_type TEXT, payload TEXT, processed_at DATETIME DEFAULT CURRENT_TIMESTAMP)";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("[SystemEventHandler] Error initializing DB: " + e.getMessage());
        }
    }

    public void handleEvent(String eventType, String payload) {
        System.out.println("[SystemEventHandler] Received event: Type=" + eventType + ", Payload=" + payload);

        if (eventType.equals("USER_CREATED")) {
            System.out.println("[SystemEventHandler] Handling USER_CREATED event...");
            updateUserCountInDb();
            sendWelcomeEmail(payload);
            logEvent("UserCreatedHandled", payload);
        } else if (eventType.equals("ORDER_PLACED")) {
            System.out.println("[SystemEventHandler] Handling ORDER_PLACED event...");
            updateSalesMetricsInDb(payload);
            logEvent("OrderPlacedHandled", payload);
        } else {
            System.out.println("[SystemEventHandler] Unknown event type: " + eventType);
            logEvent("UnknownEvent", payload);
        }

        saveEventToDb(eventType, payload);
        System.out.println("[SystemEventHandler] Event saved to DB.");
    }

    private void updateUserCountInDb() {
        System.out.println("[SystemEventHandler] Updating user count in DB " + dbUrl + "...");

        String sql = "UPDATE metrics SET user_count = user_count + 1";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            pstmt.executeUpdate();
        } catch (SQLException e) {
            System.err.println("[SystemEventHandler] Database error updating user count: " + e.getMessage());
        }
    }

    private void updateSalesMetricsInDb(String payload) {
        System.out.println("[SystemEventHandler] Updating sales metrics in DB " + dbUrl + " with payload: " + payload + "...");

        String sql = "UPDATE metrics SET total_sales = total_sales + ?";
        try (Connection conn = DriverManager.getConnection(dbUrl);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {
            double amount = Double.parseDouble(payload); 
            pstmt.setDouble(1, amount);
            pstmt.executeUpdate();
        } catch (SQLException | NumberFormatException e) {
            System.err.println("[SystemEventHandler] Database/Parsing error updating sales metrics: " + e.getMessage());
        }
    }

    private void sendWelcomeEmail(String recipientEmail) {
        System.out.println("[SystemEventHandler] Sending welcome email to " + recipientEmail + " (SMTP: " + smtpHost + ")...");

        Properties props = new Properties();
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.host", smtpHost);
        props.put("mail.smtp.port", "587");

        Session session = Session.getInstance(props, new Authenticator() {
            protected PasswordAuthentication getPasswordAuthentication() {
                return new PasswordAuthentication("welcome@example.com", "password"); 
            }
        });

        try {
            Message message = new MimeMessage(session);
